
public class Multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2;
		int j=5;
		System.out.println(i*j);
		System.out.println(i-j);
		System.out.println(i+j);
		System.out.println(i/j);
		System.out.println("Below are the basic math program result in Java");
		System.out.println();
	}

}
